#!/usr/bin/python# -*- coding: utf-8 -*-class LogPro():    def __init__(self):        self.logFileName = 'log.txt'      #k线的时间段大小         self.funcLogName = 'funcLog.txt'    def funcRunLog(self,plog):        f = open(self.funcLogName,'a')        f.write(str(plog) + '\n')        f.close()    def newFileLog(self,logFileName,dat):        f = open(logFileName,'a')        f.write(str(dat)+'\n')        f.close()