#-*- coding: utf-8 -*-import os,sysimport configToolfrom dirUtil import cur_file_dirconfigfile = 'config.txt'#获取脚本路径def cur_file_dir():    pathx = sys.argv[0]    path,_file = os.path.split(pathx)    if cmp(path,'') == 0:        path = sys.path[0]    #判断为脚本文件还是py2exe编译后的文件，如果是脚本文件，则返回的是脚本的目录，如果是py2exe编译后的文件，则返回的是编译后的文件路径    if os.path.isdir(path):        return path    elif os.path.isfile(path):        return os.path.dirname(path)#获取父目录def GetParentPath(strPath):    if not strPath:        return None;        lsPath = os.path.split(strPath);    if lsPath[1]:        return lsPath[0];        lsPath = os.path.split(lsPath[0]);    return lsPath[0];def getConfigDic():    cf = configTool.ConfigParser()    cf.read(cur_file_dir() + os.sep + configfile)#     s = cf.sections()  #     print 'section:', s  #     o = cf.options('baseconf')  #     print 'options:', o  #     v = cf.items('baseconf')  #     print 'db:', v  #     v = cf.items('parameter')  #     print 'db:', v  # [baseconf]  # [parameter]# ;价格网格宽度# step_price=0.5# ;网格等值配置资金# step_num=0.01# ;跌穿强买币所占总持币的比例# force_sellpecent=0.1# [test]# ;当前是测试模式还是实盘交易模式# istest=1# ;测试模式的基本cny# tbase_cny=1500.00# ;测试模式的基本btc# tbase_btc=0.0    access_key = cf.get('baseconf','access_key')    secret_key = cf.get('baseconf','secret_key')    step_price = cf.getfloat('parameter', 'step_price')    step_num = cf.getfloat('parameter', 'step_num')    force_sellpecent = cf.getfloat('parameter', 'force_sellpecent')        istest = cf.getint('test', 'istest')    tbase_cny = cf.getfloat('test', 'tbase_cny')    tbase_btc = cf.getfloat('test', 'tbase_btc')    return {'access_key':access_key,'secret_key':secret_key,'step_price':step_price,'step_num':step_num,'force_sellpecent':force_sellpecent,'istest':bool(istest),'tbase_cny':tbase_cny,'tbase_btc':tbase_btc}#主方法def main():    getConfigDic()if __name__ == '__main__':    main()