#!/usr/bin/env python# -*- coding: utf-8 -*-import os,sys#获取脚本路径def cur_file_dir():    pathx = sys.argv[0]    path,_file = os.path.split(pathx)    if cmp(path,'') == 0:        path = sys.path[0]    #判断为脚本文件还是py2exe编译后的文件，如果是脚本文件，则返回的是脚本的目录，如果是py2exe编译后的文件，则返回的是编译后的文件路径    if os.path.isdir(path):        return path    elif os.path.isfile(path):        return os.path.dirname(path)def getSaveDir():    pahtmp = cur_file_dir() + os.sep + 'res'    if not os.path.exists(pahtmp):        os.mkdir(pahtmp)    return pahtmpdef saveDatToFile(datstr,fname,wtype = 'add'):    if wtype == 'add':        f = open(getSaveDir() + os.sep + fname,'a')        f.write(datstr)        f.close()    elif wtype == 'new':        f = open(getSaveDir() + os.sep + fname,'w')        f.write(datstr)        f.close()